mapper.select:
    	/* retrieveWrkList */

		SELECT WRK_NM
		     , SOURCE
		FROM ROBOT02
		WHERE USER_ID = #{userId}

		AND WRK_NM = #{wrkNm}

    	/* retrieveWrkList */

		SELECT WRK_NM
			 , SOURCE
		     , DATE_FORMAT(CHNG_HISTRY_DTTM,'%Y-%m-%d %H:%i:%s') AS	CHNG_HISTRY_DTTM
		FROM ROBOT02
		WHERE USER_ID = #{userId}

		AND WRK_NM = #{wrkNm}
